import React, { Component } from 'react';
import './App.css'; // Import specific CSS file for Log In component

class ContactUs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contactUsPage: null,
      forms: null,
      name: '',
      email: '',
      message: '',
    };
  }

  componentDidMount() {
    fetch('http://localhost:3001/forms', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ pageName: 'ContactUs' }),
    })
      .then((res) => res.json())
      .then((data) => {
        this.setState({ contactUsPage: data.pageData, forms: data.forms });
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { name, email, message } = this.state;

    fetch('http://localhost:3001/send-email', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name, email, message }),
    })
      .then((response) => response.text())
      .then((result) => {
        console.log(result);
        alert('Email sent successfully');
      })
      .catch((error) => {
        console.error('Error:', error);
        alert('Error sending email');
      });
  };

  render() {
    const { contactUsPage, forms, name, email, message } = this.state;
    if (!contactUsPage || !forms) {
      return <div>Loading...</div>;
    }

    return (
      <div className="contactUs">
        <div className="contactUsPage">
          <form onSubmit={this.handleSubmit}>
            <h2>{contactUsPage.pageName}</h2>
            {forms.fields.map((field, index) => (
              <div key={index}>
                <label>{field.label}</label>
                {field.type === 'textarea' ? (
                  <textarea
                    name={field.name}
                    placeholder={field.placeholder}
                    value={this.state[field.name]}
                    onChange={this.handleChange}
                  ></textarea>
                ) : (
                  <input
                    type={field.type}
                    name={field.name}
                    placeholder={field.placeholder}
                    value={this.state[field.name]}
                    onChange={this.handleChange}
                  />
                )}
              </div>
            ))}
            <button type="submit" className="contactUs-button">
              Send Message
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default ContactUs;
