import React, { Component } from 'react';
import './App.css'; 
import signPic from './images/signup.png';

class SignUp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      signupPage: null,
      forms: null,
    };
  }

  componentDidMount() {
    // Fetch form data from the server
    fetch('http://localhost:3001/pages/SignUp') // Adjust the endpoint URL as needed
    .then((res =>         
       res.json()
    ))
    .then(data => {
      this.setState({ signupPage: data});
    })
    fetch('http://localhost:3001/forms/signUpForm') // Adjust the endpoint URL as needed
      .then((res => 

         res.json()
      ))
      .then(data => {
        console.log(data);
        this.setState({ forms: data });
      })

  }

  render() {
    const { forms, signupPage } = this.state;
    if(!forms || !signupPage){
      return <div>Loaging</div>
    }

    return (
      <div className="signUp-container">
        {/* Left side */}
        <div className="signUp-left-side">
          <p>{signupPage.title}</p>
          <img src={signPic} alt={signupPage.pageTitle} />
        </div>

        {/* Right side */}
        <div className="signUp-right-side">
          <div className='signUp-form-container'>
            <h2 className="signUp-form-title">{forms.pageName}</h2>
            {forms.fields.map((item, index) => (
              <div className="signUp-input-group" key={index}>
                <label>{item.label}</label>
                <input
                  type={item.type}
                  name={item.name}
                  placeholder={item.placeholder}
                />
              </div>
            ))}
            <button className="signUp-button" type={forms.buttons[0].type}>
              {forms.buttons[0].label}
            </button><br/> <br/>
            <text>{forms.text}{' '}
              <a href={forms.buttons[1].url}>
                {forms.buttons[1].label}
              </a>
            </text>
            <div className="signUp-or-text">{forms.text1}</div>
            <div className="signUp-social-buttons">
              {forms.socialMediaIcons.map((icon, index) => (
                <button key={index}>
                  <a href={icon.iconTextUrl}>
                    {icon.iconText}
                  </a>
                </button>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default SignUp;
