import React, { Component } from 'react';
import './App.css'; 
import signPic from './images/signup.png';

class SignUp extends Component {
  componentDidMount() {
    // Make a GET request to fetch data from the server
    fetch('http://localhost:3000/pages/3') // Adjust the endpoint URL as needed
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        console.log(data); // Handle the response data
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }

  render() {
    return (
      <div className="signUp-container">
        {/* Left side */}
        <div className="signUp-left-side">
          {/* Fetching page title from pagesData */}
          <p>{this.state.pageTitle}</p>
          {/* Assuming pagesData is imported */}
          <img src={signPic} alt={this.state.pageTitle} />
        </div>

        {/* Right side */}
        <div className="signUp-right-side">
          <div className='signUp-form-container'>
            {/* Assuming pagesData is imported */}
            <h2 className="signUp-form-title">{this.state.pageName}</h2>
            {/* Assuming formsData is imported */}
            {formsData.fields.map((item, index) => (
              <div className="signUp-input-group" key={index}>
                <label>{item.label}</label>
                <input
                  type={item.type}
                  name={item.name}
                  placeholder={item.placeholder}
                />
              </div>
            ))}
            {/* Assuming formsData is imported */}
            <button className="signUp-button" type={formsData.buttons[0].type}>
              {formsData.buttons[0].label}
            </button><br/> <br/>
            {/* Assuming formsData is imported */}
            <text>{formsData.text}{' '}
              <a href={formsData.buttons[1].url}>
                {formsData.buttons[1].label}
              </a>
            </text>
            {/* Assuming formsData is imported */}
            <div className="signUp-or-text">{formsData.text1}</div>
            {/* Assuming formsData is imported */}
            <div className="signUp-social-buttons">
              <button>
                <a href={formsData.socialMediaIcons[0].iconTextUrl}>
                  {formsData.socialMediaIcons[0].iconText}
                </a>
              </button>
              <button>
                <a href={formsData.socialMediaIcons[1].iconTextUrl}>
                  {formsData.socialMediaIcons[1].iconText}
                </a>
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default SignUp;
