import React, { Component } from 'react';
import { Link } from 'react-router-dom'; 
import './App.css';

class HomePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pageTitle: "",
      pageText: "",
      buttonLabel: ""
    };
  }

  componentDidMount() {
    // Fetch pagesData
    fetch('/pages/home') // Adjust the endpoint URL as needed
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        this.setState({
          pageTitle: data.title,
          pageText: data.text,
          buttonLabel: data.button
        });
      })
      .catch(error => {
        console.error('Error fetching pagesData:', error);
      });
  }

  render() {
    const { pageTitle, pageText, buttonLabel } = this.state;

    return (
      <div className="homepage">
        <div className="homepage-text">{pageTitle}</div>
        <div className="text-below">{pageText}</div>
        <Link to="/Service">
          <button type="button" className="button">
            {buttonLabel}
          </button>
        </Link>
      </div>
    );
  }
}

export default HomePage;
