import React, { Component } from 'react';
import './App.css';
import servicePic from './images/serviceFImg.jpeg';


class Service extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pageData: null,
      loading: true
    };
  }

  componentDidMount() {
    // Fetch data from your Node.js server
    fetch('http://localhost:3000/pages/3') // Change the URL to match your server endpoint
      .then(response => response.json())
      .then(data => {
        this.setState({ pageData: data, loading: false });
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        this.setState({ loading: false });
      });
  }

  render() {
    const { pageData, loading } = this.state;

    if (loading) {
      return <div>Loading...</div>;
    }

    return (
      <div>
        <div className="service-page-container">
          <div className="service-container">
            <div className="service-left-side">
              <h1 className="service-heading">{pageData.title}</h1>
              <p className="service-sub-heading">{pageData.textTitle}</p>
              <div className="service-input-field">
                <label htmlFor="upload-input">
                  <input
                    type={pageData.field[0].type}
                    className="service-text-input"
                    id="upload-input"
                    onChange={e => console.log('file uploaded:', e.target.files[0])}
                    placeholder={pageData.field[0].placeholder}
                  />
                </label>
                <button type="button" className="service-button">
                  {pageData.buttons[0].name}
                </button>
              </div>
              <p className="service-start-journey">
                {pageData.iconText} <i className="fa-solid fa-arrow-up"></i>
              </p>
              <p className="service-textB">{pageData.textBa}</p>
            </div>
            <div className="service-right-side">
              <img src={servicePic} className="service-nice-photo" alt="Service" />
            </div>
          </div>
          <div className="service-popular-album">
            <h2 className="service-section-title">{pageData.titleAlbum}</h2>
            {pageData.images.map((image, index) => (
              <img key={index} src={image.urlRsideImg} alt={`Album ${index + 1}`} />
            ))}
          </div>
          <div className="service-popular-meditation">
            <h2 className="service-section-title">{pageData.titleMid}</h2>
            {pageData.images.slice(6, 11).map((image, index) => (
              <img key={index} src={image.urlRsideImg} alt={`Meditation ${index + 1}`} />
            ))}
          </div>
          <br />
          <br />
          <br />
          <br />
          <div className="service-how-to-use">
            <h2 className="service-section-title">{pageData.titleHTU}</h2>
            <ul className="service-instructions">
              {pageData.pList.map((item, index) => (
                <li key={index}>
                  {item.icon} {item.text}
                </li>
              ))}
            </ul>
          </div>
          <div className="service-additional-text">
            <p>{pageData.studiesTitle}</p>
          </div>
          <div className="service-article-links">
            <h2 className="service-section-title">{pageData.textArtR}</h2>
            <ul className="service-links">
              {pageData.links.map((link, index) => (
                <li key={index}>
                  <a href={link.url}>{link.name}</a>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default Service;
