import React, { Component } from 'react';
import './App.css';

class ContactUs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contactUsPage: null,
      forms: null,
    };
  }

  componentDidMount() {
    // Fetch pagesData
    fetch('http://localhost:3001/forms/ContactUs') // Adjust the endpoint URL as needed
      .then((res) => res.json())
      .then((data) => {
        this.setState({ contactUsPage: data.pageData, forms: data.forms });
      });
  }

  render() {
    const { contactUsPage, forms } = this.state;
    if (!contactUsPage || !forms) {
      return <div>loading</div>;
    }

    return (
      <div className='contactUs'>
        <div className='contactUsPage'>
          <form>
            <h2>{contactUsPage.pageName}</h2>
            {forms.fields.map((field, index) => (
              <div key={index}>
                {field.type === 'textarea' ? (
                  <textarea
                    name={field.name}
                    placeholder={field.placeholder}
                  ></textarea>
                ) : (
                  <input
                    type={field.type}
                    name={field.name}
                    placeholder={field.placeholder}
                  />
                )}
              </div>
            ))}
              <a href={forms.url} className="contactUs-button" >
                {forms.label}
              </a>
          </form>
        </div>
      </div>
    );
  }
}

export default ContactUs;
