import React, { Component } from 'react';
import './App.css'; // Import specific CSS file for Log In component

class LogIn extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loginpage : null,
      forms: null,
    };
  }

  componentDidMount() {
    // Fetch formsData
    fetch('http://localhost:3001/forms/LogInForm') // Adjust the endpoint URL as needed
      .then((res =>         
         res.json()
      ))
      .then(data => {
        this.setState({ loginpage: data});
      })

          // Fetch formsData
    fetch('http://localhost:3001/forms/LogInForm') // Adjust the endpoint URL as needed
    .then((res => 

       res.json()
    ))
    .then(data => {
      this.setState({ forms: data });
    })
  }

  render() {
    const { loginpage } = this.state;
    if(!loginpage){
      return <div>loading</div>
    }

    return (
      <div className='logIn'>
        <div className='logIn-form'>
          <form>
            <h2 className="logIn-title">{loginpage.textL}</h2>
            {forms.fields.map((field, index) => (
              <div className="logIn-input-container" key={index}>
                <label>{field.label}</label>
                <input
                  type={field.type}
                  name={field.name}
                  placeholder={field.placeholder}
                  className="logIn-input"
                />
              </div>
            ))}
            <div className="logIn-forgetpass"><text><a href='/'>{forms.text}</a></text></div>
            <button className="logIn-login-button">{forms.button}</button><br/><br/>
            <text className="logIn-text1">{forms.text1}</text>
            <div className="logIn-googleB-centered">
              <button className="logIn-google-button"><a href={forms.buttonUrl1}>{forms.button1}</a></button>
            </div>
            <div className="logIn-bottom-createA">
              <text>{forms.text2} <a href={forms.text3Url}>{forms.text3}</a></text>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default LogIn;
