import React, { Component } from 'react';
import './App.css';

class ContactUs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contactUsPage : null ,
      forms : null,
    };
  }

  componentDidMount() {
    // Fetch pagesData
    fetch('http://localhost:3001/pages/ContactUs') // Adjust the endpoint URL as needed
      .then((res => 
         res.json()
      ))
      .then(data => {
        this.setState({ contactUsPage: data });
      })

    // Fetch formsData
    fetch('http://localhost:3001/forms/ContactUsForm') // Adjust the endpoint URL as needed
      .then((res => 

         res.json()
      ))
      .then(data => {
        this.setState({ forms: data });
      })

  }

  render() {
    const { contactUsPage, forms } = this.state;
    if(!contactUsPage || !forms){
      return <div>loading</div>
    }

    return (
      <div className='contactUs'>
        <div className='contactUsPage'>
          <form>
            <h2>{contactUsPage.pageName}</h2>
            {forms.fields.map((field, index) => (
              <div key={index}>
                <label>{field.label}</label>
                {field.type === 'textarea' ? (
                  <textarea
                    name={field.name}
                    placeholder={field.placeholder}
                  ></textarea>
                ) : (
                  <input
                    type={field.type}
                    name={field.name}
                    placeholder={field.placeholder}
                  />
                )}
              </div>
            ))}
            <button type={forms.buttons[0].type}>{forms.buttons[0].label}</button>
          </form>
        </div>
      </div>
    );
  }
}

export default ContactUs;
