import React, { Component } from 'react';
import './App.css';

class ContactUs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contactUsPage : null ,
    };
  }

  componentDidMount() {
    // Fetch pagesData
    fetch('http://localhost:3001/pages/contactUs') // Adjust the endpoint URL as needed
      .then(res => {
        return res.json();
      })
      .then(data => {
        this.setState({ pageName: data.pageName });
      })
      .catch(error => {
        console.error('Error fetching pagesData:', error);
      });

    // Fetch formsData
    fetch('/forms/contactUsForm') // Adjust the endpoint URL as needed
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        const fields = data.fields.map(field => ({
          label: field.label,
          type: field.type,
          name: field.name,
          placeholder: field.placeholder
        }));
        const button = data.buttons[0];
        this.setState({
          fields: fields,
          buttonType: button.type,
          buttonLabel: button.label
        });
      })
      .catch(error => {
        console.error('Error fetching formsData:', error);
      });
  }

  render() {
    const { pageName, fields, buttonType, buttonLabel } = this.state;

    return (
      <div className='contactUs'>
        <div className='contactUsPage'>
          <form>
            <h2>{pageName}</h2>
            {fields.map((field, index) => (
              <div key={index}>
                <label>{field.label}</label>
                {field.type === 'textarea' ? (
                  <textarea
                    name={field.name}
                    placeholder={field.placeholder}
                  ></textarea>
                ) : (
                  <input
                    type={field.type}
                    name={field.name}
                    placeholder={field.placeholder}
                  />
                )}
              </div>
            ))}
            <button type={buttonType}>{buttonLabel}</button>
          </form>
        </div>
      </div>
    );
  }
}

export default ContactUs;
