import React, { Component } from 'react';
import './App.css'; 

class About extends Component {
  constructor(props) {
    super(props);
    this.state = {
      aboutPage: null
    };
  }

  componentDidMount() {
    const pageName = "about";
    // Make a GET request to fetch data from the server
    fetch(`http//localhost:3001/pages/${pageName}`) // Adjust the endpoint URL as needed
      .then(response => {

        response.json();
      })
      .then(pages => {
        this.setState({ aboutPage: pages });
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }

  render() {
    const { aboutPage } = this.state;
    if(!aboutPage){
return <div>...loading</div>
    }

    return (
      <div className="about">
        <div className="about-content">
          {aboutPage && (
            <>
              <div className="about-title">{aboutPage.title}</div>
              <p>{aboutPage.text}</p>
            </>
          )}
        </div>
      </div>
    );
  }
}

export default About;
