import React, { Component } from 'react';
import './App.css'; // Import specific CSS file for Log In component

class LogIn extends Component {
  constructor(props) {
    super(props);
    this.state = {
      textL: "",
      fields: [],
      text: "",
      button: "",
      text1: "",
      button1: "",
      buttonUrl1: "",
      text2: "",
      text3: "",
      text3Url: ""
    };
  }

  componentDidMount() {
    // Fetch formsData
    fetch('http://localhost:3000/forms/LogInForm') // Adjust the endpoint URL as needed
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        const form = data;
        this.setState({
          textL: form.textL,
          fields: form.fields,
          text: form.text,
          button: form.button,
          text1: form.text1,
          button1: form.button1,
          buttonUrl1: form.buttonUrl1,
          text2: form.text2,
          text3: form.text3,
          text3Url: form.text3Url
        });
      })
      .catch(error => {
        console.error('Error fetching formsData:', error);
      });
  }

  render() {
    const { textL, fields, text, button, text1, button1, buttonUrl1, text2, text3, text3Url } = this.state;

    return (
      <div className='logIn'>
        <div className='logIn-form'>
          <form>
            <h2 className="logIn-title">{textL}</h2>
            {fields.map((field, index) => (
              <div className="logIn-input-container" key={index}>
                <label>{field.label}</label>
                <input
                  type={field.type}
                  name={field.name}
                  placeholder={field.placeholder}
                  className="logIn-input"
                />
              </div>
            ))}
            <div className="logIn-forgetpass"><text><a href='/'>{text}</a></text></div>
            <button className="logIn-login-button">{button}</button><br/><br/>
            <text className="logIn-text1">{text1}</text>
            <div className="logIn-googleB-centered">
              <button className="logIn-google-button"><a href={buttonUrl1}>{button1}</a></button>
            </div>
            <div className="logIn-bottom-createA">
              <text>{text2} <a href={text3Url}>{text3}</a></text>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default LogIn;
