import React, { Component } from 'react';
import './App.css';

class ContactUs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contactUsPage : null ,
    };
  }

  componentDidMount() {
    // Fetch pagesData
    fetch('http://localhost:3001/pages/contactUs') // Adjust the endpoint URL as needed
      .then(res => {
         res.json();
      })
      .then(data => {
        this.setState({ pageName: data.pageName });
      })
      .catch(error => {
        console.error('Error fetching pagesData:', error);
      });

    // Fetch formsData
    fetch('http://localhost:3001/forms/contactUsForm') // Adjust the endpoint URL as needed
      .then(response => {

         response.json();
      })
      .then(data => {
        this.setState({ contactUsPage: data });
      })

  }

  render() {
    const { contactUsPage } = this.state;
    if(!contactUsPage){
      return <div>loading</div>
    }

    return (
      <div className='contactUs'>
        <div className='contactUsPage'>
          <form>
            <h2>{pageName}</h2>
            {fields.map((field, index) => (
              <div key={index}>
                <label>{field.label}</label>
                {field.type === 'textarea' ? (
                  <textarea
                    name={field.name}
                    placeholder={field.placeholder}
                  ></textarea>
                ) : (
                  <input
                    type={field.type}
                    name={field.name}
                    placeholder={field.placeholder}
                  />
                )}
              </div>
            ))}
            <button type={buttonType}>{buttonLabel}</button>
          </form>
        </div>
      </div>
    );
  }
}

export default ContactUs;
