import React, { Component } from 'react';
import { Link } from 'react-router-dom'; 
import './App.css';

class HomePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      homepage: null,
      error: null
    };
  }

  componentDidMount() {
    // Fetch homepage data from the server
    fetch('/pages/home') // Adjust the endpoint URL as needed
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        this.setState({ homepage: data, error: null });
      })
      .catch(error => {
        this.setState({ error: 'Error fetching homepage data' });
        console.error('Error fetching homepage data:', error);
      });
  }

  render() {
    const { homepage, error } = this.state;

    if (error) {
      return <div>Error: {error}</div>;
    }

    return (
      <div className="homepage">
        {homepage && (
          <>
            <div className="homepage-text">{homepage.title}</div>
            <div className="text-below">{homepage.text}</div>
            <Link to="/Service">
              <button type="button" className="button">
                {homepage.button}
              </button>
            </Link>
          </>
        )}
      </div>
    );
  }
}

export default HomePage;
