import React, { Component } from 'react';
import './App.css'; 
import signPic from './images/signup.png';

class SignUp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      formData: {}
    };
  }

  componentDidMount() {
    // Fetch form data from the server
    fetch('http://localhost:3000/forms/1') // Adjust the endpoint URL as needed
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        this.setState({ formData: data });
      })
      .catch(error => {
        console.error('Error fetching form data:', error);
      });
  }

  render() {
    const { formData } = this.state;

    return (
      <div className="signUp-container">
        {/* Left side */}
        <div className="signUp-left-side">
          <p>{formData.pageTitle}</p>
          <img src={signPic} alt={formData.pageTitle} />
        </div>

        {/* Right side */}
        <div className="signUp-right-side">
          <div className='signUp-form-container'>
            <h2 className="signUp-form-title">{formData.pageName}</h2>
            {formData.fields && formData.fields.map((item, index) => (
              <div className="signUp-input-group" key={index}>
                <label>{item.label}</label>
                <input
                  type={item.type}
                  name={item.name}
                  placeholder={item.placeholder}
                />
              </div>
            ))}
            <button className="signUp-button" type={formData.buttons[0].type}>
              {formData.buttons[0].label}
            </button><br/> <br/>
            <text>{formData.text}{' '}
              <a href={formData.buttons[1].url}>
                {formData.buttons[1].label}
              </a>
            </text>
            <div className="signUp-or-text">{formData.text1}</div>
            <div className="signUp-social-buttons">
              {formData.socialMediaIcons && formData.socialMediaIcons.map((icon, index) => (
                <button key={index}>
                  <a href={icon.iconTextUrl}>
                    {icon.iconText}
                  </a>
                </button>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default SignUp;
